import { Component, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Driver } from 'src/app/models/Driver';
import { DriverService } from 'src/app/services/Driver.service';

@Component({
  selector: 'app-add-driver',
  templateUrl: './add-driver.component.html',
  styleUrls: ['./add-driver.component.scss']
})
export class AddDriverComponent implements OnInit {
  @ViewChild('driverForm') DriverForm?: NgForm;
  IsNew:boolean=true;
  id:any;
   drivers: Driver = {
      id:'',
     firstName: '',
     lastName: '',
     email: '',
     phoneNumber:''
     };
   submitted = false;
 
   constructor(private driverservice: DriverService,private activeRoute: ActivatedRoute,private router:Router) {
     activeRoute.params.subscribe(params => {
    this.id=params['id'];
    if(this.id){
      console.log(this.id);
      
     this.IsNew=false;
    this.driverservice.get(this.id).subscribe(a=>{
      console.log(a.result);
      if(a.isSuccess){

        console.log(a.result);

      this.drivers=a.result||new Driver();
      this.id=a.result?.id;
      }else
      {
        alert(a.errorMessages)
      }
    })
    }
    console.log(this.id);
    
     });
    }
 
   ngOnInit(): void {
   }
 
   savedrivers(): void {
    console.log(this.DriverForm?.form.value);
    
     if(this.DriverForm?.valid){
      if(!this.id){
        console.log("create")
        console.log()
       

     this.driverservice.create(this.drivers)
       .subscribe(
         response => {
           console.log(response);
           if(response.isSuccess)
           {
            this.submitted = true;
           
            this.router.navigate(['/drivers']);
           }
           else
           {
            alert(response.errorMessages)
           }
    

         },
         error => {
           console.log(error);
         });
        }else{
          //update
          console.log("update")
          this.driverservice.update(this.id,this.drivers)
          .subscribe(
            response => {
              console.log(response);
              if(response.isSuccess)
              {
              this.submitted = true;
              this.router.navigate(['/drivers']);
              }else
              {
                alert(response.errorMessages)
              }
            },
            error => {
              console.log(error);
            });
        }
        }else{
         console.log(this.DriverForm);
         
       }
   }
   updatedrivers(){}
   newdrivers(): void {
     this.submitted = false;
 
   }
 
 }
 
<div>
  <div class="submit-form">
    <div *ngIf="!submitted">

      <form #driverForm="ngForm">
        <div class="form-group justify-content-center">
          <label for="firstName" class="form-label">First Name</label>
          <input class="form-control" id="firstName" required [(ngModel)]="drivers.firstName" name="firstName"
            [ngClass]="{'border-red': driverForm.submitted&&driverForm['controls'].firstName.errors?.required}" />
          <div *ngIf="driverForm['controls'].firstName?.touched&&driverForm['controls'].firstName.errors?.required"
            class="text-warning">
            First Name is Required
          </div>
        </div>
        <div class="form-group">
          <label for="lastName">Last Name</label>
          <input type="text" class="form-control" id="lastName" required [(ngModel)]="drivers.lastName" name="lastName"
            [ngClass]="{'border-red': driverForm.submitted&&driverForm['controls'].lastName.errors?.required}" />
          <div *ngIf="driverForm['controls'].lastName?.touched&&driverForm['controls'].lastName.errors?.required"
            class="text-danger">
            Last Name is Required
          </div>
        </div>
        <div class="form-group">
          <label for="description">Email</label>
          <input class="form-control" id="email" required [(ngModel)]="drivers.email" name="email"
            [ngClass]="{'border-red': driverForm.submitted&&driverForm['controls'].email.errors?.required}" />
          <div *ngIf="driverForm['controls'].email?.touched&&driverForm['controls'].email.errors?.required"
            class="text-danger">
            Email is Required
          </div>
        </div>
        <div class="form-group">
          <label for="phoneNumber">Phone Number</label>
          <input class="form-control" id="description" required [(ngModel)]="drivers.phoneNumber" name="phoneNumber"
            [ngClass]="{'border-red': driverForm?.submitted&&driverForm['controls'].phoneNumber.errors?.required}" />
          <div *ngIf="driverForm['controls'].phoneNumber?.touched&&driverForm['controls'].phoneNumber.errors?.required"
            class="text-danger">
            Phone Number is Required
          </div>
        </div>

        <button type="submit" (click)="savedrivers()" class="btn btn-primary">Submit</button>

      </form>



      <form  #driverForm="ngForm" >
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">Email address</label>
          <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
          <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
        </div>
        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">Password</label>
          <input type="password" class="form-control" id="exampleInputPassword1">
        </div>
        <div class="mb-3 form-check">
          <input type="checkbox" class="form-check-input" id="exampleCheck1">
          <label class="form-check-label" for="exampleCheck1">Check me out</label>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>
    </div>
  </div>
</div>
